"use strict";function page(){new Header,new ScrollBtn,new Nav(0),new Footer}new page;var login={name:$("#userName"),password:$("#password"),btn:$("#loginBtn"),init:function(){this.btn.on("click",$.proxy(this.startLogin,this))},startLogin:function(){var n=this.name.val(),t=this.password.val();$.ajax({type:"post",url:"../php/login.php",data:{username:n,password:t},dataType:"json",success:function(n){alert(n.info),2==n.status&&(location.href="index.html")},error:function(n){alert("系统错误")}})}};login.init();